import React from 'react'
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';

class MovieCard extends React.Component{
  state = {
    title: "",
    rating: ""
  };

  callApi = () => {
    axios.get("http://www.omdbapi.com/?apikey=dc43bd63&t=moana").then((response) => {
      console.log(response.data)
      let title = response.data;
      let rating = "";
      this.setState({
        title: title, 
        rating: rating
      })
    })
  }

  handleOnClick = e => {
    this.callApi();
  }

  render(){
    return (
      <Row>
        <Col>
            <div className="movieCard">
                <h3 className="movieData" onClick={this.handleOnClick}>{this.state.title}</h3>
                <h4 className="movieData">{this.state.rating}</h4>
            </div>
        </Col>
      </Row>
    )
  }
}

  export default MovieCard;
