import React from 'react'
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';

class MovieCard extends React.Component{
  constructor(){
    super();
    this.state = {
      title: "",
      rating: "",
      input: ''
    }; 
    this.handleChange = this.handleChange.bind(this);
}


handleChange(event){
    this.setState({
        input: event.target.value
    });
}




  callApi = () => {
    const movieSearch = this.handleChange();

    axios.get("http://www.omdbapi.com/?apikey=dc43bd63&s=" + movieSearch).then((response) => {
      console.log(response.data)
      // let title = response.data.Title;
      // let rating = response.data.Rated;
      const movieTitleList = response.data.results.map(obj => obj.title);
      const movieReleaseDate = response.data.results.map(obj => obj.release_date)
      console.log(movieTitleList)
      console.log(movieReleaseDate)
      this.setState({
        title: movieTitleList, 
        rating: movieReleaseDate
      })
    })
  }



  handleOnClick= () => this.callApi()


  render(){
    return (
      <>
      <form onSubmit={this.handleSubmit}>
            <label>
             <input type="text" value={this.state.input} onChange={this.handleChange} placeholder="Enter Movie title" />
            </label>
             <input type="submit" value="Submit" />
      </form>
      <Row>
        <Col>
            {/* <div className="movieCard">
                <h3 className="movieData" onClick={this.handleOnClick}>{"Title: " + this.state.title}</h3>
                <h4 className="movieData">{"Rated: " + this.state.rating}</h4>
            </div> */}
        </Col>
      </Row>
      </>
    )
  }
}

  export default MovieCard;
