import React from 'react'
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';

class MovieCard extends React.Component{
  state = {
    title: "",
    rating: ""
  };



  callApi = () => {

    axios.get("https://api.themoviedb.org/3/movie/now_playing?api_key=69e5edb77f38725cd72dd9cff4d08612&language=en-US&page=1").then((response) => {
      console.log(response.data)
      let title = response.data.Title;
      let rating = response.data.Rated;
      const movieTitleList = response.data.results.map(obj => obj.Title);
      const movieRatingList = response.data.results.map(obj => obj.Rated)
      console.log(movieList)
      this.setState({
        title: movieTitleList, 
        rating: rating
      })
    })
  }

  // sortMovies(){
  //   let randomMovieTitle = (Math.random() * (this.state.title.length));
  //   this.setState({
  //     title: randomMovieTitle
  //   })
  // }

  handleOnClick = e => {
    this.callApi();
  }

  render(){
    return (
      <Row>
        <Col>
            <div className="movieCard">
                <h3 className="movieData" onClick={this.handleOnClick}>{"Title: " + this.state.title}</h3>
                <h4 className="movieData">{"Rated: " + this.state.rating}</h4>
            </div>
        </Col>
      </Row>
    )
  }
}

  export default MovieCard;
