import React from 'react'
import axios from 'axios';
import {
    Card, ListGroup, ListGroupItem, Row, Col
  } from 'react-bootstrap';



class MovieCard extends React.Component{
  state ={
    title: "",
    rating: ""
  };

  callApi = () => {
    axios.get("http://www.omdbapi.com/?apikey=dc43bd63&t").then((response) => {
      let title = response.data.t[0];
      let rating = '';
      this.setState({
        title: title, 
        rating: rating
      })
    })
  }

  render(){
    return (
      <Row>
        <Col>
            <div className="movieCard">
                <h3 className="movieData">{this.state.title}</h3>
                <h4 className="movieData">Rating: </h4>
            </div>
        </Col>
      </Row>
    );
  }
}

  export default MovieCard;
