      console.log(movieTitleList)
      console.log(movieReleaseDate)

    })
  }  


return (
      <>
      <form>
          <label>
          <input type="text" value={input} onChange={searchValue} placeholder="Enter Movie title" />
          </label>
           <input type="submit" value="Search" onClick={callApi} />
      </form>
      <Row>
        <Col>
             <div className="movieCard">
                <h3 className="movieData">Title: </h3>
