import React from 'react'
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';

class MovieCard extends React.Component{
  state = {
    title: "",
    rating: ""
  };



  callApi = () => {

    axios.get("http://www.omdbapi.com/?apikey=dc43bd63&t=[9]").then((response) => {
      console.log(response.data)
      let title = response.data.Title;
      let rating = response.data.Rated;
      this.setState({
        title: title, 
        rating: rating
      })
    })
    // axios.get("http://www.omdbapi.com/?apikey=dc43bd63&t=").then(response => response.json()).then((response) => {
    //   let movieList = response.data;
    //   console.log(movieList)
    //   this.setState({
    //     title: movieList.title,
    //     rating: movieList.rating
    //   })
    // })
  }

  sortMovies(){
    let randomMovie = (Math.random() * (this.state.title.length));
    this.setState({
      title: randomMovie
    })
  }

  handleOnClick = e => {
    this.callApi();
  }

  render(){
    return (
      <Row>
        <Col>
            <div className="movieCard">
                <h3 className="movieData" onClick={this.handleOnClick}>{"Title: " + this.state.title}</h3>
                <h4 className="movieData">{"Rated: " + this.state.rating}</h4>
            </div>
        </Col>
      </Row>
    )
  }
}

  export default MovieCard;
